import React , {useState} from 'react';
import './App.css';
import Nav from './Components/Nav/Nav';
import Body from './Components/Body/Body';
import Footer from './Components/Footer/Footer';

function App() {
  const [contactData, setContactData] = useState([
    {
      id: 0,
      firstName: 'NIK',
      lastName: 'DJETELI',
      age: '28',
      updateMode: false
    },
    {
      id: 1,
      firstName: 'Derick',
      lastName: 'Rose',
      age: '38',
      updateMode: true
    }
  ]);

  const [newContactData , setNewContactData] = useState([

  ]);



  // Handle input changes in the form. 
  const handleInputEvent = (event) => {
    event.preventDefault()
    const target  = event.target;
    const name = target.name;
    const value = target.value;
    const id = target.id;
    const targetObject = contactData;
    const newData = targetObject.map( (el) => {
      if ( name.toString() === 'firstName' && el.id === parseInt(id) ) { 
        el.firstName = value; 
      } else { 
        if ( name.toString() === 'lastName'  && el.id === parseInt(id)) { 
          el.lastName = value; 
        } else { 
          if ( name.toString() === 'age' && el.id === parseInt(id) ) {
            el.age = value; 
          } 
        }
      }
      return el;
    });
    setContactData(newData);
  };

  const editContact = ( id ) => {
    console.log(id);
  };

  const deleteContact = ( id ) => {
    console.log(id);
  };

  const saveNewContact = ( id ) => {
    console.log(id);
  };

  const addNewContact = () => {
    const id = newContactData.length === 0 ? (contactData.length+1):
               (newContactData[newContactData.length-1].id + 1);
    // console.log(id);
    const data = [{
      id: id,
      firstName: '',
      lastName: '',
      age: '',
      updateMode: true
    }];
    let result = [...newContactData , ...data];
    setNewContactData(result);
    console.log(result);
  }

  return (
    <div className="App">
      <Nav></Nav>
      <Body contactData={ contactData }  
            editContact={ editContact }
            deleteContact={ deleteContact }
            saveNewContact={ saveNewContact }
            newContactData={ newContactData }
            addNewContact={ addNewContact }
            handleInputEvent={ handleInputEvent } >
      </Body>
      <Footer></Footer>
    </div>
  );

}

export default App;
