import React from 'react';
import './Body.css';

const Body = ( { data , handleInputEvent , editContact, deleteContact ,  saveNewContact , newContactData , addNewContact } ) => {

    const showContact = data.map((el , index) => {
        let display = el.updateMode === true? { display: 'inline-block' } : { display: 'none' };
        let labelDisplay = el.updateMode === true? { display: 'none' } : { display: 'inline-block' };
       return ( <tbody key={ index } >
                    <tr>
                        <th scope="row"> { index+1 } </th>
                        <td>
                            <label style={ labelDisplay }> {el.firstName} </label>
                            <input type="text" 
                                   style={ display } 
                                   className="form-control" 
                                   name="firstName"
                                   value = { el.firstName }
                                   onChange={ handleInputEvent }
                                   id= {index}
                                   placeholder="First Name" />
                        </td>
                        <td>
                            <label style={ labelDisplay } > {el.lastName} </label>
                            <input type="text" 
                                   style={ display } 
                                   className="form-control" 
                                   name="lastName"
                                   value = { el.lastName }
                                   onChange={ handleInputEvent }
                                   id= {index}
                                   placeholder="Last Name" />
                        </td>
                        <td>
                            <label style={ labelDisplay } > {el.age} </label> 
                            <input type="text" 
                                   style={ display } 
                                   className="form-control" 
                                   name="age"
                                   value = { el.age }
                                   onChange={ handleInputEvent }
                                   id= {index}
                                   placeholder="Age" />
                        </td>
                        <td>
                            <i className="fa fa-edit" 
                               style={ labelDisplay} 
                               onClick={ () => editContact(index) }></i>
                            <i className="fa fa-trash" 
                               style={ labelDisplay } 
                               onClick={ () => deleteContact(index) } ></i>
                            <button type="button" 
                                    id="save-button" 
                                    style={ display } 
                                    className="btn btn-primary">Save</button>
                        </td>
                    </tr>
                </tbody>);
    });

    const newContact = newContactData.map((el , index) => {
    //    let display = el.updateMode === true? { display: 'block' } : { display: 'none' };
       return ( <tbody key={ index }>
                    <tr>
                        <th scope="row"> { index+1 } </th>
                        <td>
                            <input type="text" 
                                   className="form-control" 
                                   name="firstName"
                                   value = { el.firstName }
                                   onChange={ handleInputEvent }
                                   id= {index}
                                   placeholder="First Name" />
                        </td>
                        <td>
                            <input type="text" 
                                   className="form-control" 
                                   name="lastName"
                                   value = { el.lastName }
                                   onChange={ handleInputEvent }
                                   id= {index}
                                   placeholder="Last Name" />
                        </td>
                        <td>
                            <input type="text" 
                                   className="form-control" 
                                   name="age"
                                   value = { el.age }
                                   onChange={ handleInputEvent }
                                   id= {index}
                                   placeholder="Age" />
                        </td>
                        <td>
                            <button type="button" 
                                    id="save-button" 
                                    onClick={ () => saveNewContact(index) }
                                    className="btn btn-primary">Save</button>
                        </td>
                    </tr>
                </tbody>);
    });


    return (
        <div>
            <div className="container">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">First Name</th>
                            <th scope="col">Last Name</th>
                            <th scope="col">Age</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    { showContact }
                    { newContact }
                </table>
                <div className="add-new-contact"><a href="#" onClick={ addNewContact }><span className="plus">+</span> Add new contact</a></div>
            </div>
        </div>
    );
}

export default Body;
