import React from 'react';
import './Body.css';

const Body = ( { data , handleInputEvent } ) => {

    const showContact = data.map((el , index) => {
        let display = el.updateMode === true? { display: 'block' } : { display: 'none' };
        let labelDisplay = el.updateMode === true? { display: 'none' } : { display: 'block' };
       return ( <tbody key={ index } >
                    <tr>
                        <th scope="row"> { index+1 } </th>
                        <td>
                            <label style={ labelDisplay }> {el.firstName} </label>
                            <input type="text" 
                                   style={ display } 
                                   className="form-control" 
                                   name="fistName"
                                   value = { el.firstName }
                                   onChange={ handleInputEvent }
                                   id= {index}
                                   placeholder="First Name" />
                        </td>
                        <td>
                            <label style={ labelDisplay } > {el.lastName} </label>
                            <input type="text" 
                                   style={ display } 
                                   className="form-control" 
                                   name="lastName"
                                   value = { el.lastName }
                                   onChange={ handleInputEvent }
                                   id= {index+1}
                                   placeholder="Last Name" />
                        </td>
                        <td>
                            <label style={ labelDisplay } > {el.age} </label> 
                            <input type="text" 
                                   style={ display } 
                                   className="form-control" 
                                   name="age"
                                   value = { el.age }
                                   onChange={ handleInputEvent }
                                   id= {index+1}
                                   placeholder="Age" />
                        </td>
                        <td>
                            <i className="fa fa-edit"></i>
                            <i className="fa fa-trash"></i>
                        </td>
                    </tr>
                </tbody>);
    });

    return (
        <div>
            <div className="container">
                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">First Name</th>
                            <th scope="col">Last Name</th>
                            <th scope="col">Age</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    { showContact }
                    <tbody>
                        <tr>
                            <th scope="row">4</th>
                            <td>
                                <input type="text" className="form-control" id="first-name-new" placeholder="Fist Name" />
                            </td>
                            <td>
                                <input type="text" className="form-control" id="last-name-new" placeholder="Last Name" />
                            </td>
                            <td>
                                <input type="text" className="form-control" id="age-new" placeholder="Age" />
                            </td>
                            <td>
                                <button type="button" id="save-button-new" className="btn btn-primary">Save</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <div className="add-new-contact"><a href="#"><span className="plus">+</span> Add new contact</a></div>
            </div>
        </div>
    );
}

export default Body;
