import React , {useState} from 'react';
import './App.css';
import Nav from './Components/Nav/Nav';
import Body from './Components/Body/Body';
import Footer from './Components/Footer/Footer';

function App() {
  const [data , setData] = useState([
    {
      id: 0,
      firstName: 'NIK',
      lastName: 'DJETELI',
      age: '28',
      updateMode: false
    },
    {
      id: 1,
      firstName: 'Derick',
      lastName: 'Rose',
      age: '38',
      updateMode: false
    },
    {
      id: 2,
      firstName: 'Patrick',
      lastName: 'OMO',
      age: '20',
      updateMode: true
    },
    {
      id: 3,
      firstName: 'George',
      lastName: 'MAKUM',
      age: '19',
      updateMode: false
    }
  ]);

  // Handle input changes in the form. 
  const handleInputEvent = (event) => {
    event.preventDefault()
    const target  = event.target;
    const name = target.name;
    const value = target.value;
    const id = target.id;
    console.log(value);
    console.log(name)
    console.log(target);
    console.log(id);
    const targetObject = data;
    const newData = targetObject.map( (el) => {
      if ( name.toString() == 'firstName' && el.id == id ) { 
        el.firstName = 'test'; 
      } else { 
        if ( name.toString() == 'lastName'  && el.id == id ) { 
          el.lastName = 'testoo'; 
        } else { 
          if ( name.toString() == 'age' && el.id == id ) {
            el.age = '100'; 
          } 
        }
      }
      return el;
    });
    console.log(newData);
    // const  newData = data.map;
    // setData({
    //     [name] : value
    // });
  }

  return (
    <div className="App">
      <Nav></Nav>
      <Body data={ data }  
            handleInputEvent={ handleInputEvent } >
      </Body>
      <Footer></Footer>
    </div>
  );

}

export default App;
