import React , {useState} from 'react';
import './App.css';
import Nav from './Components/Nav/Nav';
import Body from './Components/Body/Body';
import Footer from './Components/Footer/Footer';

function App() {
  const [data , setData] = useState([
    {
      id: 1,
      firstName: 'NIK',
      lastName: 'DJETELI',
      age: '28',
      updateMode: false
    },
    {
      id: 2,
      firstName: 'Derick',
      lastName: 'Rose',
      age: '38',
      updateMode: false
    },
    {
      id: 3,
      firstName: 'Patrick',
      lastName: 'OMO',
      age: '20',
      updateMode: true
    },
    {
      id: 4,
      firstName: 'George',
      lastName: 'MAKUM',
      age: '19',
      updateMode: false
    }
  ]);

  // Handle input changes in the form. 
  const handleInputEvent = (event) => {
    const target  = event.target;
    const name = target.name;
    const value = target.value;
    console.log(value);
    // setData({
    //     [name] : value
    // });
  }

  return (
    <div className="App">
      <Nav></Nav>
      <Body data={ data }  handleInputEvent={ handleInputEvent } ></Body>
      <Footer></Footer>
    </div>
  );

}

export default App;
